import React from "react";
import styled from "styled-components";
import { NavLink } from "react-router-dom";

const variables = {
  blueColor: "#0062ff",
  grayColor: "#92929d",
};

const active = "nav-item-active";
const NavItem = styled(NavLink).attrs({
  active,
})`
  display: flex;
  align-items: center;
  text-decoration: none;
  color: #171725;
  font-size: 1.2rem;
  letter-spacing: 0.1px;
  border-left: 3px solid #fff;
  svg {
    fill: ${variables.grayColor};
  }
  &.${active} {
    color: ${variables.blueColor};
    border-left: 3px solid ${variables.blueColor};
    svg {
      fill: ${variables.blueColor};
    }
  }
`;
const Wrapper = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 40px;
`;
const Icon = styled.div`
  margin: 0 24px;
`;
const NameLink = styled.span`
  @media (max-width: 620px) {
    display: none;
  }
`;

interface IItemProps {
  name: string;
  icon: object | string;
  link: string;
}

const Item: React.FC<IItemProps> = (props) => {
  const { icon, name, link } = props;

  return (
    <Wrapper>
      <NavItem exact activeClassName={active} to={link}>
        <Icon>{icon}</Icon>
        <NameLink>{name}</NameLink>
      </NavItem>
    </Wrapper>
  );
};

export default Item;
